import React, { useRef, useState } from "react";
import { Editor, Viewer } from "@toast-ui/react-editor";
import { useDispatch, useSelector } from "react-redux";
import { PostsState, postActions } from "../../store";

interface Post {
  post: PostsState;
}

const NewPost = () => {
  const editRef = useRef<Editor>(null);
  const posts = useSelector((state: Post) => state.post.post);
  const dispatch = useDispatch();

  const [markdown, setMarkdown] = useState<string | undefined>("# ㄴㄷㅌ");

  const clickHandler = () => {
    console.log(editRef.current?.getInstance().getHTML());
    console.log(editRef.current?.getInstance().getMarkdown());

    console.log(markdown);
    setMarkdown(editRef.current?.getInstance().getMarkdown());
    dispatch(
      postActions.addPost({
        id: 4,
        title: "Hello",
        author: "Sunnyjin",
        text: editRef.current?.getInstance().getMarkdown(),
      })
    );
  };

  return (
    <div>
      <Editor previewStyle="vertical" height="600px" ref={editRef} />
      <button onClick={clickHandler}>HIHI</button>
    </div>
  );
};

export default NewPost;
