import React, { useEffect, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import "../../styles/Blog.css";
import { IPostState, PostsState } from "../../store/index";
import Card from "../layout/Card";
import { Link } from "react-router-dom";
import axios from "axios";
import { postActions } from "../../store/index";

interface Post {
  post: PostsState;
}

const Blog: React.FC = () => {
  const postList = useSelector((state: Post) => state.post.post);
  const dispatch = useDispatch();

  const useIsMounted = () => {
    const isMounted = useRef(false);
    useEffect(() => {
      isMounted.current = true;
    }, []);
    return isMounted;
  };

  // In your component check if it's mounted
  // ...because you cannot set state on unmounted component
  const isMounted = useIsMounted();

  useEffect(() => {
    const getPost = async () => {
      await axios.get("/posts").then((res) => {
        res.data.posts.map((list: any) => {
          console.log(list.title);
          dispatch(
            postActions.addPost({
              id: list._id,
              title: list.title,
              author: list.slug,
              text: list.description,
              markdown: list.markdown,
            })
          );
        });
      });
    };
    getPost();
  }, [isMounted]);

  return (
    <div className="card-container">
      {postList.map((list: IPostState) => (
        <Card
          id={list.id}
          title={list.title}
          author={list.author}
          text={list.text}
        />
      ))}
      <Link to="/newpost">
        <button className="postBtn">글쓰기</button>
      </Link>
    </div>
    // <div>
    //   <div>
    //     <p>Title</p>
    //     <p>Author</p>
    //     <p>Text</p>
    //   </div>
    // </div>
  );
};

export default Blog;
